import { useState } from 'react';
import Header from './Header';
import PostList from './PostsList';
import Form from './Form';
import data from '../data/data.json';
import '../stylesheet/index.scss';

const App = () => {
  const [posts] = useState(data);

  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [color, setColor] = useState('');

  const handleInput = (inputId, inputValue) => {
    console.log(`Ha cambiado el ${inputId} con el valor: ${inputValue}`);
    if (inputId === 'email') {
      setEmail(inputValue);
    } else if (inputId === 'name') {
      setName(inputValue);
    } else if (inputId === 'color') {
      setColor(inputValue);
    }
  };

  const handleReset = () => {
    console.log('Han pulsado en Reset');
    setEmail('');
    setName('');
    setColor('');
  };

  return (
    <div>
      <Header title="Las maravillas del mundo" />
      <PostList postsList={posts} />

      <Form handleInput={handleInput} />
    </div>
  );
};

export default App;
