import { useState, useRouteMatch } from 'react';
import { Link, Route, Switch } from 'react-router-dom';

import Header from './Header';
import PostList from './PostsList';
import Form from './Form';
import Home from './Home';

import PostId from './PostId.js';
import data from '../data/data.json';
import '../stylesheet/index.scss';

const App = () => {
  const [posts, setPosts] = useState(data);
  const [name, setName] = useState('');
  const [desc, setDesc] = useState('');
  const [country, setCountry] = useState('');
  const [img, setImg] = useState('');

  const routeData = useRouteMatch('/product/:productId');

  const handleInput = (value, id) => {
    if (id === 'name') {
      setName(value);
    } else if (id === 'description') {
      setDesc(value);
    } else if (id === 'description') {
      setDesc(value);
    } else if (id === 'country') {
      setCountry(value);
    } else if (id === 'img') {
      setImg(value);
    }
  };

  const newPost = () => {
    const data = {
      id: `e-${posts.length + 1}`,
      name: name,
      country: country,
      imageUrl: img,
      description: desc,
    };
    posts.push(data);
    setPosts([...posts]);
  };

  // esta función solo se ejecuta cuando estamos en la ruta /product/:productId
  // esta función funciona como un componente de React en sí mismo y recibe unas props
  const renderPostId = (routerProps) => {
    // ReactRouterDom me pasa routerProps con toda la info que él cree que puedo necesitar
    // - si he puesto <Router path="/product/:productId"> tengo el id del producto en routerProps.match.params.productId
    // - si he puesto <Router path="/product/:id"> tengo el id del producto en routerProps.match.params.id
    console.log('Id del producto de la ruta', routerProps.match.params.postId);
    // obtengo el id del producto desde la ruta
    const routerPostId = routerProps.match.params.postId;
    // busco el producto en el listado de productos a través del id de la ruta
    const postFound = posts.find((post) => post.id === routerPostId);
    console.log(postFound);
    // siempre debo comprobar si el producto ha sido encontrado
    if (postFound) {
      // renderizo el producto y se le paso por props
      return <PostId post={postFound} />;
    } else {
      // renderizo el producto no encontrado o no renderizo nada
      //return <ProductNotFound />;
    }
  };
  return (
    <div>
      <Header title="Las maravillas del mundo" />
      <Form
        name={name}
        desc={desc}
        img={img}
        handleInput={handleInput}
        newPost={newPost}
      />

      <Switch>
        <Route path="/posts">
          <PostList postsList={posts} />
        </Route>

        {/* la prop render del componente Route puede ser una función u otro 
        componente */}
        <Route path="/posts/:postId">
          <PostId routeData={routeData} />
        </Route>

        <Route path="/" exact>
          {/* aquí puedo poner todo el código JSX que quiera */}
          <Home />
        </Route>
      </Switch>
    </div>
  );
};

export default App;
