import { useState } from 'react';
import { Route, Switch, useRouteMatch } from 'react-router-dom';

import Header from './Header';
import PostList from './PostsList';
import Form from './Form';
import Home from './Home';

import PostId from './PostId.js';
import data from '../data/data.json';
import '../stylesheet/index.scss';

const App = () => {
  const [posts, setPosts] = useState(data);
  const [name, setName] = useState('');
  const [desc, setDesc] = useState('');
  const [country, setCountry] = useState('');
  const [img, setImg] = useState('');

  const handleInput = (value, id) => {
    if (id === 'name') {
      setName(value);
    } else if (id === 'description') {
      setDesc(value);
    } else if (id === 'description') {
      setDesc(value);
    } else if (id === 'country') {
      setCountry(value);
    } else if (id === 'img') {
      setImg(value);
    }
  };

  const newPost = () => {
    const data = {
      id: `e-${posts.length + 1}`,
      name: name,
      country: country,
      imageUrl: img,
      description: desc,
    };
    posts.push(data);
    setPosts([...posts]);
  };

  return (
    <div>
      <Header title="Las maravillas del mundo" />
      <Form
        name={name}
        desc={desc}
        img={img}
        handleInput={handleInput}
        newPost={newPost}
      />

      <Switch>
        <Route path="/posts">
          <PostList postsList={posts} />
        </Route>

        {/* la prop render del componente Route puede ser una función u otro 
        componente */}
        <Route path="/posts/:postId">
          <PostId routeData={routeData} />
        </Route>

        <Route path="/" exact>
          {/* aquí puedo poner todo el código JSX que quiera */}
          <Home />
        </Route>
      </Switch>
    </div>
  );
};

export default App;
